}

function update(c, buf) {
  var l = buf.length;

  for (var n = 0; n < l; n++) {
    var e = crc_table[c[3] ^ buf[n]];
    c[3] = e[3] ^ c[2];
    c[2] = e[2] ^ c[1];
    c[1] = e[1] ^ c[0];
    c[0] = e[0];
  }
}

