import React, { Component, useState } from 'react'
import { Form, FormGroup, Input, Container, Button, Modal, ModalHeader, ModalBody, ModalFooter, ButtonGroup } from 'reactstrap'
import fileDownload from 'js-file-download'
// import './style.css'
// const { dialog } = window.require('electron').remote

class DownloadHtmlTemplateModal extends Component {
  constructor(props) {
    super(props)

    this.toggle = this.toggle.bind(this)
    this.save = this.save.bind(this)
    // this.nextButtonClick = this.nextButtonClick.bind(this)
    // this.onNextClick = this.onNextClick.bind(this)
    // this.cancelButtonClick = this.cancelButtonClick.bind(this)
    // this.onCancelClick = this.onCancelClick.bind(this)

    this.state = {
      modal: false,
      folder: this.props.config.userData+'/',
      filename: 'template.html',
    }
  }

  componentDidMount() {

  }

  toggle() {

    this.setState({ modal: !this.state.modal })
    // if (this.props.onNextClick) this.props.onNextClick()
    // if (this.props.onCancelClick) this.props.onCancelClick()
  }

  save() {
    dialog.showOpenDialog({
      properties: ['openFile', 'multiSelections']
      }, function (files) {
          if (files !== undefined) {
              // handle files
          }
      })
  }

  render() {
    return(
      <div>
        <Button color="danger" onClick={this.toggle}>Save</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>{this.props.title}</ModalHeader>
          <ModalBody>
            <div id="">
              Please select a custom HTML template to replace the default.
              <Container className="p-5" >
                <Form id="modalFormLeft">
                  <FormGroup>
                    <div id="upload_button">
                      <label>
                        <input directory="" type="file" id="ma"
                          onChange={e => this.setState({ folder: document.getElementsByTagName('input')[0].files[0].path })}
                        />
                        <span class="btn btn-primary">Choose Path</span>
                        {' '+this.state.folder}
                      </label>
                    </div>

                  </FormGroup>
                </Form>
              </Container>
            </div>
          </ModalBody>
          <ModalFooter>
            <ButtonGroup>
              <Button color="warning" onClick={this.saveFile}>Save</Button>{' '}
              <Button color="warning" onClick={this.props.onCancelClick ? this.props.onCancelClick : this.toggle}>{this.props.cancelButtonText}</Button>
            </ButtonGroup>
          </ModalFooter>
        </Modal>
      </div>
    )
  }
}
export default DownloadHtmlTemplateModal
